axios.get('/api/get_promocodes')
   .then(function (response) {
      var res = JSON.parse(JSON.stringify(response.data));
      var div_data = "<option value='null'>Select Promo Code</option>";
      for(i = 0; i < res.data.length; i++){
         console.log('Date created: ', res.data[i]);
         div_data = div_data + "<option value="+res.data[i].id+">"+res.data[i].promocode_title+"</option>";
      }
      $('#promocode').html(div_data);
   })
   .catch(function (error) {
      if (error.response) { // get response with a status code not in range 2xx
         console.log(error.response.status);
      } else if (error.request) { // no response
         console.log(error.request);
      } else { // Something wrong in setting up the request
         console.log('Error', error.message);
      }
      console.log(error.config);
   });

axios.get('/api/product/1')
   .then(function (response) {
      var res = JSON.parse(JSON.stringify(response.data));
      //console.log('Product Data res: ', res);
      //console.log('Product Data name: ', res.data.name);
      $('#unitPriceDisplay').html(res.data.unitPrice);
      $('#unitPrice').val(res.data.unitPrice);
      $('#productName').val(res.data.name);
      $('#productDesc').val(res.data.description);
      $('#shippingCharge').val(res.data.shipping_charge);
      $('#shippingChargeDisplay').html(res.data.shipping_charge);

   })
   .catch(function (error) {
      if (error.response) { // get response with a status code not in range 2xx
         console.log(error.response.data);
      } else if (error.request) { // no response
         console.log(error.request);
      } else { // Something wrong in setting up the request
         console.log('Error', error.message);
      }
   });
